    server_name localhost;

    location / {
        root /usr/share/nginx/html;
        index index.html;
        try_files $uri $uri/ /index.html;
    }

    # Redirigir /dashboard a la raÃ­z (para corregir el problema)
    location /dashboard {
        return 301 /;
    }

    # Para solicitudes API, proxy al backend
    location /api {
        proxy_pass http://app-server:3000;
